contracts/core/DappadVesting.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "../interfaces/Interfaces.sol";
import "../errors/Errors.sol";

contract DappadVesting is AccessControl {
    bytes32 public constant OWNER = keccak256("OWNER");
    bytes32 public constant ADMIN = keccak256("ADMIN");

    IDappadVesting.Vesting public defaultVesting;
    IDappadVesting.VestingSettings public vestingSettings;
    IDappadVesting.VestingAddresses public vestingAddresses;
    IDappadVesting.VestingStatistics public vestingStatistics;

    mapping(address => IDappadVesting.Vesting) public customVestings;
    mapping(address => mapping(uint256 => bool)) public vestingClaimed;
    mapping(address => uint256) public investmentAmounts;

    IERC20Extented public saleToken;
    IDappadSale public saleContract;

    event UserClaimedVesting(address userAddress, uint256 vestingIndex, uint256 amount, uint256 saleTokenAmount, uint256 totalVestingAmount, uint256 claimedTime);
    event UserClaimedAllUnlockedVestings(address userAddress, uint256 amount, uint256 saleTokenAmount, uint256 totalVestingAmount, uint256 claimedTime);
    event SaleTokenAdded(address user, uint256 saleAmount, uint256 totalSaleTokenAmount);
    event CustomVestingUpdated(address userAddress, IDappadVesting.Vesting vesting, uint256[] vestingPercentages);
    event TotalVestingAmountUpdated(uint256 totalVestingAmount);
    event DefaultVestingUpdated(IDappadVesting.Vesting vesting, uint256[] vestingPercentages);
    event TotalSaleTokenAmountUpdated(uint256 totalSaleTokenAmount);
    event VestingAddressesUpdated(IDappadVesting.VestingAddresses vestingAddresses);
    event UserInvestmentAmountUpdated(address userAddress, uint256 amount);
    event UserInvestmentAmountsUpdated(address[] userAddresses, uint256[] amounts);

    constructor(IDappadVesting.VestingParams memory vestingParams) 
        notZeroAddress(vestingParams.vestingAddresses.saleTokenAddress) 
        notZeroAddress(vestingParams.vestingAddresses.factoryContractAddress) 
        notZeroAddress(vestingParams.vestingAddresses.ownerAddress) 
        notZeroAddress(vestingParams.vestingAddresses.adminAddress) 
    {
        _setRoleAdmin(OWNER, OWNER);
        _setRoleAdmin(ADMIN, OWNER);

        _grantRole(OWNER, vestingParams.vestingAddresses.ownerAddress);
        _grantRole(ADMIN, vestingParams.vestingAddresses.adminAddress);

        if (vestingParams.defaultVesting.vestingPercentages.length == 0) revert Errors.EmptyVestingPercentageError();
        if (vestingParams.defaultVesting.vestingDuration <= 0) revert Errors.InvalidVestingDurationError();

        defaultVesting = vestingParams.defaultVesting;

        saleToken = IERC20Extented(vestingParams.vestingAddresses.saleTokenAddress);

        if (vestingParams.vestingAddresses.saleContractAddress != address(0)) {
            saleContract = IDappadSale(vestingParams.vestingAddresses.saleContractAddress);
        }

        vestingSettings = vestingParams.vestingSettings;

        vestingAddresses = vestingParams.vestingAddresses;

        uint256 totalVestingPercentage = 0;

        defaultVesting.vestingPercentages = new uint256[](vestingParams.defaultVesting.vestingPercentages.length);

        for(uint256 i = 0; i < vestingParams.defaultVesting.vestingPercentages.length; i++) {
            defaultVesting.vestingPercentages[i] = vestingParams.defaultVesting.vestingPercentages[i];
            totalVestingPercentage += vestingParams.defaultVesting.vestingPercentages[i];
        }

        if (totalVestingPercentage != 10000) revert Errors.TotalVestingPercentageError();
    }

    modifier notZeroAddress(address _address) {
        _notZeroAddress(_address);
        _;
    }

    modifier onlyOwner(address _address) {
        _onlyOwner(_address);
        _;
    }

    modifier onlyOwnerOrAdmin(address _address) {
        _onlyOwnerOrAdmin(_address);
        _;
    }

    modifier hasEnoughAllowance(IERC20Extented token, address userAddress, uint256 amount) {
        _hasEnoughAllowance(token, userAddress, amount);
        _;
    }

    function claimVesting(uint256 vestingIndex) external notZeroAddress(msg.sender) returns (bool) {
        if(vestingAddresses.saleContractAddress != address(0)) {
            if (saleContract.getUserInvestmentAmount(msg.sender) == 0) revert Errors.NotJoinedSaleError();

            if (saleContract.getRefundEnabled()) {
                if (saleContract.getUserRefunded(msg.sender)) revert Errors.AlreadyRefundedError();
            }
        } else {
            if (investmentAmounts[msg.sender] == 0) revert Errors.NotJoinedSaleError();
        }

        if (vestingClaimed[msg.sender][vestingIndex]) revert Errors.AlreadyClaimedError();

        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[msg.sender].vestingCount > 0) {
            currentVesting = customVestings[msg.sender];
        }

        if (block.timestamp <= vestingSettings.startDate) revert Errors.VestingsNotClaimableError();

        bool claimable = getVestingClaimable(msg.sender, vestingIndex);
        if (!claimable) revert Errors.VestingNotClaimableError();

        vestingClaimed[msg.sender][vestingIndex] = true;

        uint256 saleTokenAmount = getUserTotalVestingAmount(msg.sender);
        uint256 claimAmount = (saleTokenAmount * currentVesting.vestingPercentages[vestingIndex]) / 10000;

        if(claimAmount == 0) revert Errors.VestingNotClaimableError();

        if (saleToken.balanceOf(address(this)) < claimAmount) revert Errors.InsufficientContractBalanceError();

        vestingStatistics.totalVestingAmount -= claimAmount;

        emit UserClaimedVesting(msg.sender, vestingIndex, claimAmount, saleTokenAmount, vestingStatistics.totalVestingAmount, block.timestamp);

        bool successfullyTransfered = saleToken.transfer(msg.sender, claimAmount);
        if (!successfullyTransfered) revert Errors.TokenTransferFailedError();

        return true;
    }

    function claimAllUnlockedVestings() external notZeroAddress(msg.sender) returns (bool) {
        if(vestingAddresses.saleContractAddress != address(0)) {
            if (saleContract.getUserInvestmentAmount(msg.sender) == 0) revert Errors.NotJoinedSaleError();

            if (saleContract.getRefundEnabled()) {
                if (saleContract.getUserRefunded(msg.sender)) revert Errors.AlreadyRefundedError();
            }
        } else {
            if (investmentAmounts[msg.sender] == 0) revert Errors.NotJoinedSaleError();
        }

        uint256 saleTokenAmount = getUserTotalVestingAmount(msg.sender);
        uint256 claimAmount = 0;

        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[msg.sender].vestingCount > 0) {
            currentVesting = customVestings[msg.sender];
        }

        if (block.timestamp <= vestingSettings.startDate) revert Errors.VestingsNotClaimableError();

        for (uint256 i = 0; i < currentVesting.vestingCount; i++) {
            bool claimable = getVestingClaimable(msg.sender, i);
            if (claimable) {
                claimAmount += (saleTokenAmount * currentVesting.vestingPercentages[i]) / 10000;
                vestingClaimed[msg.sender][i] = true;
            }
        }

        if(claimAmount == 0) revert Errors.VestingsNotClaimableError();

        if (saleToken.balanceOf(address(this)) < claimAmount) revert Errors.InsufficientContractBalanceError();

        vestingStatistics.totalVestingAmount -= claimAmount;

        emit UserClaimedAllUnlockedVestings(msg.sender, claimAmount, saleTokenAmount, vestingStatistics.totalVestingAmount, block.timestamp);

        bool successfullyTransfered = saleToken.transfer(msg.sender, claimAmount);
        if (!successfullyTransfered) revert Errors.TokenTransferFailedError();

        return true;
    }

    function emergencyOnly() external notZeroAddress(msg.sender) onlyOwnerOrAdmin(msg.sender) returns (bool) {
        if(saleToken.balanceOf(address(this)) > 0) {
            bool successfullyTransfered = saleToken.transfer(msg.sender, saleToken.balanceOf(address(this)));
            if (!successfullyTransfered) revert Errors.TokenTransferFailedError();
        } 

        return true;
    }

    function addSaleToken(uint256 amount) external onlyOwnerOrAdmin(msg.sender) hasEnoughAllowance(saleToken, msg.sender, amount) returns (bool) {
        if (saleToken.balanceOf(msg.sender) < amount) revert Errors.InsufficientBalanceError();

        vestingStatistics.totalSaleTokenAmount += amount;

        emit SaleTokenAdded(msg.sender, amount, vestingStatistics.totalSaleTokenAmount);

        bool successfullyTransfered = saleToken.transferFrom(msg.sender, address(this), amount);
        if (!successfullyTransfered) revert Errors.TokenTransferFailedError();

        return true;
    }

    function setUserInvestmentAmount(address userAddress, uint256 amount) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        if (amount == 0) revert Errors.InvalidAmountError();

        investmentAmounts[userAddress] = amount;

        emit UserInvestmentAmountUpdated(userAddress, amount);

        return true;
    }

    function setUserInvestmentAmounts(address[] calldata userAddresses, uint256[] calldata amounts) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        if (userAddresses.length != amounts.length) revert Errors.ArraysLengthMismatchError();

        for (uint256 i = 0; i < userAddresses.length; i++) {
            if (amounts[i] == 0) revert Errors.InvalidAmountError();
            if (userAddresses[i] == address(0)) revert Errors.ZeroAddressError();

            investmentAmounts[userAddresses[i]] = amounts[i];
        }

        emit UserInvestmentAmountsUpdated(userAddresses, amounts);

        return true;
    }

    function setDefaultVesting(IDappadVesting.Vesting memory vesting) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        defaultVesting = vesting;

        uint256 totalVestingPercentage = 0;

        defaultVesting.vestingPercentages = new uint256[](vesting.vestingPercentages.length);
        for (uint256 i = 0; i < vesting.vestingCount; i++) {
            defaultVesting.vestingPercentages[i] = vesting.vestingPercentages[i];
            totalVestingPercentage += vesting.vestingPercentages[i];
        }

        if (totalVestingPercentage != 10000) revert Errors.TotalVestingPercentageError();

        emit DefaultVestingUpdated(vesting, vesting.vestingPercentages);

        return true;
    }

    function setCustomVesting(address userAddress, IDappadVesting.Vesting memory vesting) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        customVestings[userAddress] = vesting;

        uint256 totalVestingPercentage = 0;

        customVestings[userAddress].vestingPercentages = new uint256[](vesting.vestingPercentages.length);
        for (uint256 i = 0; i < vesting.vestingCount; i++) {
            customVestings[userAddress].vestingPercentages[i] = vesting.vestingPercentages[i];
            totalVestingPercentage += vesting.vestingPercentages[i];
        }

        if (totalVestingPercentage != 10000) revert Errors.TotalVestingPercentageError();

        emit CustomVestingUpdated(userAddress, vesting, vesting.vestingPercentages);

        return true;
    }

    function setCustomVestings(address[] calldata userAddresses, IDappadVesting.Vesting[] calldata vestings) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        if (userAddresses.length != vestings.length) revert Errors.ArraysLengthMismatchError();

        for (uint256 i = 0; i < userAddresses.length; i++) {
            if (userAddresses[i] == address(0)) revert Errors.ZeroAddressError();

            customVestings[userAddresses[i]] = vestings[i];

            uint256 totalVestingPercentage = 0;

            customVestings[userAddresses[i]].vestingPercentages = new uint256[](vestings[i].vestingPercentages.length);
            for (uint256 j = 0; j < vestings[i].vestingCount; j++) {
                customVestings[userAddresses[i]].vestingPercentages[j] = vestings[i].vestingPercentages[j];
                totalVestingPercentage += vestings[i].vestingPercentages[j];
            }

            if (totalVestingPercentage != 10000) revert Errors.TotalVestingPercentageError();

            emit CustomVestingUpdated(userAddresses[i], vestings[i], vestings[i].vestingPercentages);
        }    

        return true;
    }

    function setTotalSaleTokenAmount(uint256 amount) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        vestingStatistics.totalSaleTokenAmount = amount;

        emit TotalSaleTokenAmountUpdated(amount);

        return true;
    }

    function setTotalVestingAmount(uint256 amount) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        vestingStatistics.totalVestingAmount = amount;

        emit TotalVestingAmountUpdated(amount);

        return true;
    }

    function setVestingAddresses(IDappadVesting.VestingAddresses memory newVestingAddresses) external onlyOwnerOrAdmin(msg.sender) returns (bool) {
        vestingAddresses = newVestingAddresses;

        if (newVestingAddresses.saleTokenAddress != address(0)) {
            saleToken = IERC20Extented(newVestingAddresses.saleTokenAddress);
        }

        if (newVestingAddresses.saleContractAddress != address(0)) {
            saleContract = IDappadSale(newVestingAddresses.saleContractAddress);
        }

        emit VestingAddressesUpdated(newVestingAddresses);

        return true;
    }

    function getUserTotalVestingAmount(address userAddress) public view notZeroAddress(userAddress) returns (uint256) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        uint256 userTotalVestingAmount = 0;

        if (vestingAddresses.saleContractAddress != address(0)) {
            if (vestingSettings.saleTokenPrice > 0) {
                userTotalVestingAmount = calculateSaleTokenAmount(vestingSettings.saleTokenPrice, saleContract.getUserInvestmentAmount(userAddress));
            } else {
                userTotalVestingAmount = calculateSaleTokenAmount(saleContract.getSaleTokenPrice(), saleContract.getUserInvestmentAmount(userAddress));
            }
        } else {
            userTotalVestingAmount = calculateSaleTokenAmount(vestingSettings.saleTokenPrice, investmentAmounts[userAddress]);
        }

        return userTotalVestingAmount;
    }

    function calculateSaleTokenAmount(uint256 saleTokenPrice, uint256 investmentAmount) public view returns (uint256) {
        return ((investmentAmount / saleTokenPrice) * (10 ** saleToken.decimals()));
    }

    function getVestingClaimable(address userAddress, uint256 vestingIndex) public view returns (bool) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        if (vestingIndex >= currentVesting.vestingCount) revert Errors.InvalidVestingIndexError();

        if(vestingClaimed[userAddress][vestingIndex]) {
            return false;
        } else {
            uint256 startDate = vestingSettings.startDate;

            if(vestingIndex == 0) {
                if(currentVesting.onTGE) {
                    return true;
                } else {
                    if(currentVesting.vestingCliffDuration > 0) {
                        return block.timestamp >= (startDate + currentVesting.vestingCliffDuration);
                    } else {
                        return block.timestamp >= (startDate + (currentVesting.vestingDuration * (vestingIndex + 1)));
                    }
                }
            } else {
                if(currentVesting.vestingCliffDuration > 0) {
                    return block.timestamp >= (startDate + currentVesting.vestingCliffDuration + (currentVesting.vestingDuration * vestingIndex));
                } else {
                    return block.timestamp >= (startDate + (currentVesting.vestingDuration * (vestingIndex + 1)));
                }
            }
        }
    }

    function getAnyVestingClaimed(address userAddress) external view notZeroAddress(userAddress) returns (bool) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        for (uint256 i = 0; i < currentVesting.vestingCount; i++) {
            if (vestingClaimed[userAddress][i]) {
                return true;
            }
        }

        return false;
    }

    function getVestingClaimed(address userAddress, uint256 vestingIndex) external view notZeroAddress(userAddress) returns (bool) {
        return vestingClaimed[userAddress][vestingIndex];
    }

    function getUserUnclaimedTokenAmount(address userAddress) external view notZeroAddress(userAddress) returns (uint256) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        uint256 unclaimedTokenAmount = 0;
        uint256 userTotalVestingAmount = getUserTotalVestingAmount(userAddress);

        for (uint256 i = 0; i < currentVesting.vestingCount; i++) {
            if (!vestingClaimed[userAddress][i]) {
                unclaimedTokenAmount += (userTotalVestingAmount * currentVesting.vestingPercentages[i]) / 10000;
            }
        }

        return unclaimedTokenAmount;
    }

    function getUserLockedTokenAmount(address userAddress) external view notZeroAddress(userAddress) returns (uint256) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        uint256 lockedTokenAmount = 0;
        uint256 userTotalVestingAmount = getUserTotalVestingAmount(userAddress);

        for (uint256 i = 0; i < currentVesting.vestingCount; i++) {
            if (!vestingClaimed[userAddress][i] && !getVestingClaimable(userAddress, i)) {
                lockedTokenAmount += (userTotalVestingAmount * currentVesting.vestingPercentages[i]) / 10000;
            }
        }

        return lockedTokenAmount;
    }

    function getUserUnlockedTokenAmount(address userAddress) external view notZeroAddress(userAddress) returns (uint256) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        uint256 unlockedTokenAmount = 0;
        uint256 userTotalVestingAmount = getUserTotalVestingAmount(userAddress);

        for (uint256 i = 0; i < currentVesting.vestingCount; i++) {
            if (!vestingClaimed[userAddress][i] && getVestingClaimable(userAddress, i)) {
                unlockedTokenAmount += (userTotalVestingAmount * currentVesting.vestingPercentages[i]) / 10000;
            }
        }

        return unlockedTokenAmount;
    }

    function getNextUnlockDate(address userAddress) external view notZeroAddress(userAddress) returns (uint256) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        uint256 vestingIndex = getUserNextVestingIndex(userAddress);

        if (currentVesting.onTGE) {
            if(currentVesting.vestingCliffDuration > 0) {
                return vestingSettings.startDate + currentVesting.vestingCliffDuration + (currentVesting.vestingDuration * (vestingIndex - 1));
            } else {
                return vestingSettings.startDate + (currentVesting.vestingDuration * vestingIndex);
            }
        } else {
            if(currentVesting.vestingCliffDuration > 0) {
                return vestingSettings.startDate + currentVesting.vestingCliffDuration + (currentVesting.vestingDuration * (vestingIndex - 1));
            } else {
                return vestingSettings.startDate + (currentVesting.vestingDuration * vestingIndex);
            }
        }
    }

    function getUserNextVestingIndex(address userAddress) public view notZeroAddress(userAddress) returns (uint256) {
        IDappadVesting.Vesting memory currentVesting = defaultVesting;

        if (customVestings[userAddress].vestingCount > 0) {
            currentVesting = customVestings[userAddress];
        }

        uint256 vestingIndex = 0;

        for (uint256 i = 0; i < currentVesting.vestingCount; i++) {
            if (vestingClaimed[userAddress][i]) {
                vestingIndex = i;
            }
        }

        return vestingIndex + 1;
    }

    function getDefaultVestingPercentages() external view returns (uint256[] memory) {
        return defaultVesting.vestingPercentages;
    }

    function getCustomVestingPercentages(address userAddress) external view returns (uint256[] memory) {
        return customVestings[userAddress].vestingPercentages;
    }

    function getTotalVestingAmount() external view returns (uint256) {
        return vestingStatistics.totalVestingAmount;
    }

    function getSaleTokenAddress() external view returns (address) {
        return vestingAddresses.saleTokenAddress;
    }

    function getFactoryContractAddress() external view returns (address) {
        return vestingAddresses.factoryContractAddress;
    }

    function _notZeroAddress(address _address) internal pure {
        if (_address == address(0)) revert Errors.ZeroAddressError();
    }

    function _onlyOwner(address _address) internal view {
        if (!hasRole(OWNER, _address)) revert Errors.UnauthorizedError();
    }

    function _onlyOwnerOrAdmin(address _address) internal view {
        if (!hasRole(OWNER, _address) && !hasRole(ADMIN, _address)) revert Errors.UnauthorizedError();
    }

    function _hasEnoughAllowance(IERC20Extented token, address userAddress, uint256 amount) internal view {
        uint256 userAllowance = token.allowance(userAddress, address(this));
        if(amount > userAllowance) revert Errors.InsufficientAllowanceError();
    }
}

contracts/core/DappadVestingFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./DappadVesting.sol";
import "../interfaces/Interfaces.sol";
import "../errors/Errors.sol";

contract DappadVestingFactory is AccessControl {
    bytes32 public constant OWNER = keccak256("OWNER");
    bytes32 public constant ADMIN = keccak256("ADMIN");

    address[] public allVestings;

    event VestingCreated(
        uint256 vestingCount,
        uint256 vestingDuration,
        uint256 vestingCliffDuration,
        uint256[] vestingPercentages,
        bool onTGE,
        address saleTokenAddress,
        address saleContractAddress,
        address factoryContractAddress
    );
    event StakeContractAddressUpdated(address _stakeContractAddress);

    constructor(IDappadVestingFactory.VestingFactoryParams memory vestingFactoryParams) {
        _setRoleAdmin(OWNER, OWNER);
        _setRoleAdmin(ADMIN, OWNER);

        _grantRole(OWNER, msg.sender);
        _grantRole(ADMIN, vestingFactoryParams.adminAddress);
    }

    modifier notZeroAddress(address _address) {
        _notZeroAddress(_address);
        _;
    }

    modifier onlyOwner(address _address) {
        _onlyOwner(_address);
        _;
    }

    modifier onlyOwnerOrAdmin(address _address) {
        _onlyOwnerOrAdmin(_address);
        _;
    }

    function createVesting(IDappadVesting.VestingParams memory _vestingParams)
        public
        onlyOwnerOrAdmin(msg.sender)
        notZeroAddress(_vestingParams.vestingAddresses.saleTokenAddress)
        returns (bool)
    {
        IDappadVesting.VestingParams memory vestingParams = _vestingParams;

        if (vestingParams.defaultVesting.vestingPercentages.length == 0) {
            revert Errors.EmptyVestingPercentageError();
        }
        if (vestingParams.defaultVesting.vestingDuration == 0) {
            revert Errors.InvalidVestingDurationError();
        }

        DappadVesting newVesting = new DappadVesting(vestingParams);

        allVestings.push(address(newVesting));

        emit VestingCreated(
            vestingParams.defaultVesting.vestingCount,
            vestingParams.defaultVesting.vestingDuration,
            vestingParams.defaultVesting.vestingCliffDuration,
            vestingParams.defaultVesting.vestingPercentages,
            vestingParams.defaultVesting.onTGE,
            vestingParams.vestingAddresses.saleTokenAddress,
            vestingParams.vestingAddresses.saleContractAddress,
            address(this)
        );

        return true;
    }

    function getVestingCount() external view returns (uint256) {
        return allVestings.length;
    }

    function getVesting(uint256 index) external view returns (address) {
        if (index >= allVestings.length) {
            revert Errors.InvalidVestingIndexError();
        }
        return allVestings[index];
    }

    function _notZeroAddress(address _address) internal pure {
        if (_address == address(0)) revert Errors.ZeroAddressError();
    }

    function _onlyOwner(address _address) internal view {
        if (!hasRole(OWNER, _address)) revert Errors.UnauthorizedError();
    }

    function _onlyOwnerOrAdmin(address _address) internal view {
        if (!hasRole(OWNER, _address) && !hasRole(ADMIN, _address)) revert Errors.UnauthorizedError();
    }
}

