    error NotEnabledError();

    error InsufficientBalanceError();
    error InsufficientContractBalanceError();
    error InsufficientAllowanceError();
    error UserHasNoNFTError();
    error UserNotRegisteredError();
    error UserAlreadyRegisteredError();
    error RegisterNotStartedError();
    error MaxTierInvestmentReachedError();
    error RegisterAlreadyEndedError();
    error TierOneCannotJoinStakerRoundError();

    error InvalidAmountError();
    error InvalidTierIndexError();
    error InvalidSaleStartDateError();
    error InvalidSaleEndDateError();
    error InvalidSaleDurationError();
    error InvalidMaxInvestmentAmountError();
    error InvalidVestingDurationError();
    error InvalidSaleIndexError();
    error InvalidVestingIndexError();
    error InvalidStakeContractAddressError();

    error InvalidInterestRateError();
    error InvalidLockDurationError();

    error InvalidPaymasterInputLengthError();
    error InvalidTokenError();

    error EmptySaleNameError();
    error EmptyTierDetailsError();
    error EmptyVestingPercentageError();
    error TotalVestingPercentageError();

    error FailedToAddToken();
    error SaleNotStartedError();
    error SaleNotEndedError();
    error SaleAlreadyEndedError();
    error NotJoinedSaleError();
    error AlreadyJoinedSaleError();
    error SaleIsPausedError();
    error UserHasNoNFTsError();

    error FCFSSaleNotEnabledError();
    error FCFSSaleNotStartedError();
    error FCFSSaleAlreadyStartedError();
    error FirstVestingAlreadyClaimedError();
    error VestingsNotClaimableError();

    error UserNotWhitelistedError();

    error MinimumInvestmentNotReachedError();
    error MaximumInvestmentReachedError();
    error MaxTotalInvestmentReachedError();
    error MaxNodeCountReachedError();
    error MaxNodeCountPerUserReachedError();
    error MinimumNodeTierNotReachedError();
    error MaximumNodeTierReachedError();
    error MinimumUserTierNotReachedError();

    error RefundNotEnabledError();
    error RefundPeriodOverError();
    error AlreadyRefundedError();

    error AlreadyClaimedError();
    error VestingNotClaimableError();
    error NoTierDetailsFoundError();

    error UserHasNoStakedAmountError();
    error StakePeriodNotOverError();
    error StakeIsPausedError();
    error InvalidStakeIndexError();
    error NoRewardToHarvestError();
    error NoRewardToReInvestError();
    error MinimumStakePointsNotReachedError();

    error OnlyBootloaderError();
    error TxTransferFailedError();
    error UnsupportedPaymasterFlowError();

    error EmergencyOverRewardError();
    error EmergencyOverAmountError();
}

contracts/interfaces/Interfaces.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

abstract contract IERC20Extented is IERC20 {
    function decimals() public virtual view returns (uint8); 
}

abstract contract IERC721Extented is IERC721 {
    function tokensOfOwner(address owner) external virtual view returns (uint256[] memory);
}

interface IDappadSaleFactory {
    struct SaleFactoryParams {
        address adminAddress;
    }
}

interface IDappadVestingFactory {
    struct VestingFactoryParams {
        address adminAddress;
    }
}

interface IDappadStakeFactory {
    struct StakeFactoryParams {
        address adminAddress;
    }
}

interface IDappadStakeControllerFactory {
    struct StakeControllerFactoryParams {
        address adminAddress;
    }
}

interface IDappadStakeController {
    struct StakeControllerParams {
        StakeControllerDetails stakeControllerDetails;
        StakeControllerAddresses stakeControllerAddresses;
        StakeControllerTier[] stakeControllerTiers;
    }

    struct StakeControllerDetails {
        uint256 requiredTokensForNftBonus;
        uint256 bonusEnd;
        bool addBonusPerNft;
        uint256[] bonusesPerNft;
        uint256[] stakeContractPointMultipliers;
    }

    struct StakeControllerAddresses {
        address tokenAddress;
        address ownerAddress;
        address adminAddress;
        address[] nftContractAddresses;
        address[] stakeContractAddresses;
        address[] bonusVestingContractAddresses;
    }

    struct StakeControllerTier {
        uint256 requiredStakePoints;
    }

    function getTierCount() external view returns (uint256);
    function getUserTierIndex(address userAddress) external view returns (uint256);
}

interface IDappadStake {
    struct StakeParams {
        StakeDetails stakeDetails; 
        StakeAddresses stakeAddresses;
    }

    struct StakeDetails {
        uint256 interestRate;
        uint256 lockDuration;
        uint256 lockDurationRewardMultiplier;
        uint256 lockDurationPointMultiplier;
        bool stakePaused;
    }

    struct StakeAddresses {
        address tokenAddress;
        address ownerAddress;
        address adminAddress;
    }

    struct StakeStatistics {
        uint256 rewardBalance;
        uint256 totalStakedAmount;
        uint256 totalHarvestedRewardAmount;
        uint256 totalParticipants;
    }

    struct Stake {
        uint256 endTime;
        uint256 totalStakedAmount;
        uint256 totalStakePoints;
        uint256 stakeCount;
        StakeFeatures[] stakeFeatures;
    }

    struct StakeFeatures {
        uint256 interestRate;
        uint256 stakeTime;
        uint256 stakedAmount;
    }

    function getStakeStatistics() external view returns (IDappadStake.StakeStatistics memory);
    function getUserStake(address userAddress) external view returns (IDappadStake.Stake memory);
}

interface IDappadSale {
    struct SaleParams {
        Sale sale;
        SaleTierDetails[] tierDetails;
    }

    struct Sale {
        SaleDetails saleDetails;
        SaleFcfsDetails fcfsDetails;
        SaleRefundDetails refundDetails;
        SaleTicketDetails ticketDetails;
        SaleAddresses saleAddresses;
    }

    struct SaleDetails {
        uint256 registerStart;
        uint256 registerEnd;
        uint256 registerTokenAmount;
        uint256 saleStart;
        uint256 saleEnd;
        uint256 saleTokenPrice;
        uint256 maxInvestmentAmount;
        uint256 projectOwnerFeePercentage;
        bool registerTokenEnabled;
        bool salePaused;
        string saleName;
    }

    struct SaleFcfsDetails {
        uint256 fcfsSaleDuration;
        uint256 fcfsSaleMultiplier;
    }

    struct SaleRefundDetails {
        uint256 refundDuration;
        bool refundEnabled;
    }

    struct SaleTicketDetails {
        uint256 ticketPrice;
        bytes32 ticketMerkleRoot;
    }

    struct SaleRegisterDetails {
        uint256 registerTokenAmount;
        uint256 tierIndex;
        bool registered;
    }

    struct SaleAddresses {
        address saleTokenAddress;
        address registerTokenAddress;
        address investmentTokenAddress;
        address factoryContractAddress;
        address nftContractAddress;
        address stakeControllerContractAddress;
        address vestingContractAddress;
        address ownerAddress;
        address adminAddress;
        address launchpadFeeAddress;
        address projectOwnerFeeAddress;
    }

    struct SaleStatistics {
        uint256 totalInvestmentAmount;
        uint256 totalSaleTokenAmount;
        uint256 totalParticipants;
        uint256 totalRegisters;
        uint256 totalRegisterTokenAmountLeft;
    }

    struct SaleTierDetails {
        uint256 minInvestmentAmount;
        uint256 maxInvestmentAmount;
        uint256 tierMaxInvestmentAmount;
        uint256 ticketAmount;
        bytes32 whitelistMerkleRoot;
        uint256 totalInvestmentAmount;
        uint256 totalParticipants;
    }

    function addSaleToken() external returns (bool);
    function getIsSaleActive() external view returns (bool);
    function getUserInvestmentAmount(address userAddress) external view returns (uint256);
    function getSaleEndDate() external view returns (uint256);
    function getRefundEnabled() external view returns (bool);
    function getUserRefunded(address userAddress) external view returns (bool);
    function getSaleTokenPrice() external view returns (uint256);
}

interface IDappadVesting {
    struct VestingParams {
        Vesting defaultVesting;
        VestingAddresses vestingAddresses;
        VestingSettings vestingSettings;
    }

    struct VestingSettings {
        uint256 startDate;
        uint256 saleTokenPrice;
    }

    struct Vesting {
        uint256 vestingCount;
        uint256 vestingDuration;
        uint256 vestingCliffDuration;
        uint256[] vestingPercentages;
        bool onTGE;
    }

    struct VestingAddresses {
        address saleTokenAddress;
        address factoryContractAddress;
        address saleContractAddress;
        address ownerAddress;
        address adminAddress;
    }

    struct VestingStatistics {
        uint256 totalVestingAmount;
        uint256 totalSaleTokenAmount;
    }

    function setTotalVestingAmount(uint256 amount) external returns (bool);
    function getVestingClaimed(address userAddress, uint256 vestingIndex) external view returns (bool);
    function getTotalVestingAmount() external view returns (uint256);
    function getAnyVestingClaimed(address userAddress) external view returns (bool);
    function getUserTotalVestingAmount(address userAddress) external view returns (uint256);
    function getUserUnclaimedTokenAmount(address userAddress) external view returns (uint256);
}

